// Header.js
import React, { useState } from 'react';
import './Header.css'; // Забезпечте правильний імпорт стилів

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="header-logo">
        <img src={require('../img/comfy-logo.png')} alt="" />
      </div>
      <div className={`burger-menu ${isMenuOpen ? 'open' : ''}`}>
        <div className="menu-icon" onClick={toggleMenu}>
          &#9776;
        </div>
        <div className="menu-content">
          <div className="btn-sms">
            <button>
              <a href="https://max-it-zt.github.io/comfy-zvit-teh/">
                Мій особистий контроль залу!
              </a>
            </button>
          </div>
          <button onClick={closeMenu}>Мій гаманець</button>
          <button onClick={closeMenu}>Графік на місяць</button>
        </div>
      </div>
    </header>
  );
};

export default Header;
