// src/components/PlanManager.jsx
import React, { useState } from 'react';

const PlanManager = ({ onSave }) => {
  const [itDays, setItDays] = useState(31);
  const [itPlans, setItPlans] = useState(Array(31).fill(0));
  const [hsDays, setHsDays] = useState(31);
  const [hsPlans, setHsPlans] = useState(Array(31).fill(0));

  const handleSave = () => {
    onSave({
      it: { days: itDays, plans: itPlans },
      hs: { days: hsDays, plans: hsPlans },
    });
  };

  return (
    <div>
      <h2>Плани на місяць</h2>
      <div>
        <label>Кількість днів для ІТ:</label>
        <input
          type="number"
          value={itDays}
          onChange={e => setItDays(e.target.value)}
        />
      </div>
      <div>
        <label>Плани на кожен день для ІТ:</label>
        <input
          type="text"
          value={itPlans.join(',')}
          onChange={e => setItPlans(e.target.value.split(',').map(Number))}
        />
      </div>
      <div>
        <label>Кількість днів для ХС:</label>
        <input
          type="number"
          value={hsDays}
          onChange={e => setHsDays(e.target.value)}
        />
      </div>
      <div>
        <label>Плани на кожен день для ХС:</label>
        <input
          type="text"
          value={hsPlans.join(',')}
          onChange={e => setHsPlans(e.target.value.split(',').map(Number))}
        />
      </div>
      <button onClick={handleSave}>Зберегти</button>
    </div>
  );
};

export default PlanManager;
