// Header.js
import React, { useState } from 'react';
import './Header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const closeMenu = () => {
    setIsMenuOpen(false);
  };

  return (
    <header className="header">
      <div className="header-logo">
        <img src={require('../img/comfy-logo.png')} alt="" />
      </div>
      <div
        className={`burger-menu ${isMenuOpen ? 'open' : ''}`}
        onClick={toggleMenu}
      >
        <div className="menu-icon">
          <span className={`bar ${isMenuOpen ? 'open' : ''}`}></span>
          <span className={`bar ${isMenuOpen ? 'open' : ''}`}></span>
          <span className={`bar ${isMenuOpen ? 'open' : ''}`}></span>
        </div>
        <div className="menu-content">
          <button>
            <a href="https://max-it-zt.github.io/comfy-zvit-teh/">
              Мій контроль залу!
            </a>
          </button>
          <button onClick={closeMenu}>
            <a
              href="https://capf.comfy.ua/auth/web-auth/authorize?client_id=sdo&response_type=code&state=auth&redirect_uri=https%3A%2F%2Felearning.comfy.ua%2F"
              target="_blank"
              rel="noreferrer"
            >
              Мій Гаманець
            </a>
          </button>
          <button onClick={closeMenu}>
            <a
              href="https://docs.google.com/spreadsheets/d/1Wxgk7YfdiOrgqhAZ-FJKSfee88IIFO-yeGUFCF_af6U/edit#gid=0&range=A2:AG8"
              target="_blank "
              rel="noreferrer"
            >
              Графік на місяць
            </a>
          </button>
        </div>
      </div>
    </header>
  );
};

export default Header;
