// App.js
import React, { useState, useEffect } from 'react';
import ReactModal from 'react-modal';
import itPlanData from '../data/ItPlanData';
import hsPlanData from '../data/HsPlanData';
import getCurrentDay from '../utils/Utils';
import CoefficientInput from './CoefficientInput';
import SalariesDisplay from './SalariesDisplay';
import InputField from './InputField';
import DevicesInput from './DevicesInput';
import Header from './Header';

import './App.css';

const App = () => {
  const [itSum, setItSum] = useState('');
  const [itShare, setItShare] = useState('');
  const [happySum, setHappySum] = useState('');
  const [happyShare, setHappyShare] = useState('');
  const [smartphones, setSmartphones] = useState('');
  const [laptops, setLaptops] = useState('');
  const [tvs, setTvs] = useState('');
  const [itSalaries, setItSalaries] = useState(Array(4).fill(0));
  const [coefficient, setCoefficient] = useState(() => {
    return parseFloat(localStorage.getItem('coefficient')) || 6.2;
  });

  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [itPlanInput, setItPlanInput] = useState('');
  const [happyPlanInput, setHappyPlanInput] = useState('');

  useEffect(() => {
    const itSumValue = parseFloat(itSum);
    if (!isNaN(itSumValue)) {
      const itSalariesCalculation = Array(4)
        .fill(0)
        .map((_, index) => {
          return (itSumValue * (coefficient / 100)) / (index + 1);
        });
      setItSalaries(itSalariesCalculation);
    }
  }, [itSum, coefficient]);

  useEffect(() => {
    localStorage.setItem('coefficient', coefficient.toString());
  }, [coefficient]);

  const handleCoefficientChange = e => {
    setCoefficient(parseFloat(e.target.value) || 0);
  };

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  const handlePlanSubmit = () => {
    // Додайте логіку для збереження планів в ваші файли
    const currentDay = getCurrentDay();
    // Збереження планів по ІТ
    itPlanData[currentDay - 1] = {
      day: currentDay,
      plan: parseInt(itPlanInput, 10),
    };
    // Збереження планів по Хепі
    hsPlanData[currentDay - 1] = {
      day: currentDay,
      plan: parseInt(happyPlanInput, 10),
    };

    closeModal();
  };

  const generateMessage = () => {
    // Змінений код генерації повідомлення
  };

  const handleSubmit = () => {
    const message = generateMessage();
    alert(message);
  };

  return (
    <div className="app-container">
      <div className="header-container">
        <Header />
      </div>

      {/* Введення коефіцієнта */}
      <CoefficientInput
        coefficient={coefficient}
        onCoefficientChange={handleCoefficientChange}
      />
      <div className="logo">Заробітна плата</div>
      {/* Відображення зарплат */}
      <SalariesDisplay itSalaries={itSalaries} />

      {/* Кнопка для відкриття модального вікна */}
      <button className="button" onClick={openModal}>
        Ввести плани на сьогодні
      </button>

      {/* Модальне вікно для введення планів */}
      <ReactModal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        ariaHideApp={false} // Це необхідно для уникнення помилок доступності
      >
        <label>План по ІТ:</label>
        <input
          type="text"
          value={itPlanInput}
          onChange={e => setItPlanInput(e.target.value)}
        />

        <label>План по Хепі:</label>
        <input
          type="text"
          value={happyPlanInput}
          onChange={e => setHappyPlanInput(e.target.value)}
        />

        {/* Кнопка для збереження планів і закриття модального вікна */}
        <button onClick={handlePlanSubmit}>Зберегти та закрити</button>
      </ReactModal>

      {/* ... інші компоненти та введення даних ... */}

      {/* Кнопка відправлення */}
      <button className="button" onClick={handleSubmit}>
        Відправити
      </button>
    </div>
  );
};

export default App;
